mapper.select:
SELECT b.order_id as orderId, b.branch_id as branchId, b.order_date as orderDate, b.checked as checked, b.user_name as name, u.user_no as no FROM book_order b JOIN users u ON b.user_name = u.user_name
b.branch_id = #{branchId}
AND u.user_no = #{no}
AND b.checked = #{checked}
ORDER BY b.order_id DESC

SELECT user_no AS no,
user_name AS name, branch_id AS branchId,
auth_code AS authCode
FROM users
where branch_id = #{branchId}


		bo.branch_id = #{branchId}


		AND user_no = #{no}
